INSERT INTO `product` VALUES (26, 'LIGHT BODY TYPE 3', 'LIGHT BODY TYPE 3', 71, '20200430112427_65533.jpg,20200430112431_13040.jpg,20200430112434_64166.jpg', '<h3>Light Body(TYPE 3)</h3>\n<div>\n<h3>Product Features:</h3>\n<p>①Crown,bridge,inlays and onlays impression</p>\n<p>②Recommended to take two-stage or single-stage impression technique together with Perfit Putty for higher precision</p>\n<p><strong>Specification:</strong>&nbsp;50ml/bio-cartridge*2</p>\n<p><strong>Material:&nbsp;</strong>Vinyl polysiloxane(VPS)</p>\n<p><strong>Color:</strong>&nbsp;Base(Orange),Catalyst(White)</p>\n<p><strong>Features:</strong></p>\n</div>', '<p style=\"text-align: center;\"><span style=\"font-size: 20px;\"><strong>DETAILS</strong></span></p>\n<p><strong><span style=\"font-size: 19px;\">&nbsp;</span></strong></p>\n<p style=\"text-align: center;\"><img style=\"border: 0px none;\" title=\"Light Body (TYPE 3)\" src=\"https://0.rc.xiniu.com/g1/M00/50/00/CgAGTFe9woqAYCpDAARejJZGKxQ358.png\" alt=\"Light Body (TYPE 3)\" /></p>\n<p style=\"text-align: center;\"><img style=\"border: 0px none;\" title=\"Light Body (TYPE 3)\" src=\"https://0.rc.xiniu.com/g1/M00/50/00/CgAGTFe9wqCAbFXcAAGQG--TN-0810.png\" alt=\"Light Body (TYPE 3)\" /></p>\n<p style=\"text-align: center;\"><strong><span style=\"font-family: Arial; font-size: 19px;\">&nbsp;</span></strong></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 20px;\"><strong><span style=\"font-family: Arial;\">HOW DOES PERFIT IMPRESSION LOOK LIKE?</span></strong></span></p>\n<p>&nbsp;</p>\n<p style=\"text-align: center;\"><img style=\"border: 0px none;\" title=\"Light Body (TYPE 3)\" src=\"https://0.rc.xiniu.com/g1/M00/4F/06/CgAGS1e9S6mAAOKnAAH5VdMiRcI719.png\" alt=\"Light Body (TYPE 3)\" /></p>\n<p style=\"text-align: center;\"><img style=\"border: 0px none;\" title=\"Light Body (TYPE 3)\" src=\"https://0.rc.xiniu.com/g1/M00/4F/07/CgAGS1e9S9KAKiPNAAHjjo02LuA803.png\" alt=\"Light Body (TYPE 3)\" /></p>\n<p><strong><span style=\"font-family: Arial; font-size: 19px;\">&nbsp;</span></strong></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 20px;\"><strong><span style=\"font-family: Arial;\">WHY DO YOU STILL USE ALGINATE?</span></strong></span></p>\n<p><strong><span style=\"font-size: 19px;\">&nbsp;</span></strong></p>\n<p>&nbsp;</p>\n<p style=\"text-align: center;\"><img style=\"border: 0px none;\" title=\"Light Body (TYPE 3)\" src=\"https://0.rc.xiniu.com/g1/M00/50/00/CgAGTFe9wuKAcSfsAALQUAUnWHk419.jpg\" alt=\"Light Body (TYPE 3)\" /></p>\n<p><strong><span style=\"font-size: 19px;\">&nbsp;</span></strong></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 20px;\"><strong>ACCESSORIES</strong></span></p>\n<p style=\"text-align: center;\"><strong><span style=\"font-size: 19px;\">&nbsp;</span></strong></p>\n<p>&nbsp;</p>\n<table class=\"ueditorTableStyle\" align=\"center\">\n<tbody>\n<tr class=\"firstRow\">\n<td style=\"background-color: white;\" align=\"center\" valign=\"middle\" width=\"772\">\n<p><img style=\"border: 0px none;\" title=\"Light Body (TYPE 3)\" src=\"https://0.rc.xiniu.com/g1/M00/50/3B/CgAGTFe-gpqAP2kZAABNRoJ-6UQ503.png\" alt=\"Light Body (TYPE 3)\" /></p>\n</td>\n<td style=\"background-color: white;\" align=\"center\" valign=\"middle\" width=\"772\">\n<p><img style=\"border: 0px none;\" title=\"Light Body (TYPE 3)\" src=\"https://0.rc.xiniu.com/g1/M00/50/3B/CgAGTFe-gwSAElNFAAAeGhvWvec478.jpg\" alt=\"Light Body (TYPE 3)\" /></p>\n</td>\n</tr>\n<tr>\n<td style=\"background-color: white;\" align=\"center\" valign=\"middle\" width=\"772\"><strong><span style=\"font-family: arial, helvetica, sans-serif; font-size: 20px;\">Silicone Cut</span></strong></td>\n<td style=\"background-color: white;\" align=\"center\" valign=\"middle\" width=\"772\"><strong><span style=\"font-family: arial, helvetica, sans-serif; font-size: 20px;\">Dispenser</span></strong></td>\n</tr>\n<tr>\n<td style=\"background-color: white;\" align=\"center\" valign=\"middle\" width=\"772\">\n<p>