INSERT INTO `product` VALUES (141, 'DEMCO V2 chairside CAD/CAM miller', 'DEMCO V2 chairside CAD/CAM miller', 67, '20200430115002_80509.jpg', '<p>DEMCO V2 is designed for dental chairside using</p>', '<h3 style=\"text-align: center;\"><span style=\"font-family: arial, helvetica, sans-serif;\"><strong>Product features</strong></span></h3>\n<p style=\"text-align: center;\">&nbsp;</p>\n<p style=\"text-align: center;\"><span style=\"font-family: arial, helvetica, sans-serif;\">Abandon the traditional complex equipment design,Demco uses a more optimized one-button design,one-click start and loading pins</span></p>\n<p style=\"text-align: center;\"><span style=\"font-family: arial, helvetica, sans-serif;\">and ceramic block which can more suitable for daily operation by doctors or nurses.</span></p>\n<p style=\"text-align: center;\"><span style=\"font-family: arial, helvetica, sans-serif;\">Use of open data reception(STL)which can support invarious brands of scanner connection.</span></p>\n<p style=\"text-align: center;\"><span style=\"font-family: arial, helvetica, sans-serif;\">Transmission data is simple by connecting through WI-FI,U disk,network cable and mobile hard disk.</span></p>\n<p style=\"text-align: center;\"><span style=\"font-family: arial, helvetica, sans-serif;\">Industry-class core design and stylish appearance,so that equipment with high precision and durability of the more outpatient fashion</span></p>\n<p style=\"text-align: center;\"><span style=\"font-family: arial, helvetica, sans-serif;\">equipment.</span></p>\n<p>&nbsp;</p>\n<h3 style=\"text-align: center;\"><span style=\"font-family: arial, helvetica, sans-serif;\"><strong>Mill features</strong></span></h3>\n<p style=\"text-align: center;\"><span style=\"font-family: arial, helvetica, sans-serif;\">Innovation of the regional linkage of dual-spindle technology patents</span></p>\n<p style=\"text-align: center;\"><span style=\"font-family: arial, helvetica, sans-serif;\">T1-click mounting material</span></p>\n<p style=\"text-align: center;\"><span style=\"font-family: arial, helvetica, sans-serif;\">With built-in touch screen,computers and IPD which can be connected to the monitor during the milling process</span></p>\n<p style=\"text-align: center;\"><span style=\"font-family: arial, helvetica, sans-serif;\">More stylish appearance and convenient size,that suitable for all needs of clinicians</span></p>\n<p style=\"text-align: center;\"><span style=\"font-family: arial, helvetica, sans-serif;\"><img title=\"\" src=\"https://0.rc.xiniu.com/g2/M00/1F/1B/CgAGfFxvtnyAR1XkAACaT_bd-JA648.png\" alt=\"\" /></span></p>\n<h3 style=\"text-align: center;\"><span style=\"font-family: arial, helvetica, sans-serif;\"><strong>Contact us</strong></span></h3>\n<p style=\"text-align: center;\"><span style=\"font-family: arial, helvetica, sans-serif;\">marketing@hugedental.com</span></p>\n<p style=\"text-align: center;\"><span style=\"font-family: arial, helvetica, sans-serif;\">&nbsp;</span></p>', '10', 1, 1, 1, '2020-04-30 11:50:24');
INSERT INTO `product` VALUES (172, 'VINCISMILE Invisible Orthodontic Appliance', 'VINCISMILE Invisible Orthodontic Appliance111111', 51, '20200427111716_55799.jpg,20200427111719_34393.jpg', '<h3>VINCISMILE,smile as much as you wish!&nbsp;</h3>\n<p>Vincismile Invisible Orthodontic Appliance is an elegant solution for orthodontics and&nbsp;an awesome alternative to a traditional bracket and wire system.&nbsp;</p>', '<h3>What are Vincismile Invisible Orthodontic Appliance?&nbsp;&nbsp;</h3>\n<p>Vincismile Invisible Orthodontic Appliance are vacuum formed from transparent biomedical materials, they are invisible and people even not aware patient put them on.&nbsp;Vincismileis an awesome alternative to a traditional bracket and wire system.&nbsp;</p>\n<h3>What are the advantages of Vincismile Invisible Orthodontic Appliance?&nbsp;</h3>\n<h3>More comfortable</h3>\n<p>The algorithm of Vincismilesoftware helps precisely calculate the most appropriate amount of each tooth movement, as compared to traditional brackets and wires which may irritate and cause pain.</p>\n<h3>Convenient</h3>\n<p>Vincismileclear aligners need no bands, wires and brackets, consuming less chair time, and the patient can visit the clinic less frequently (once every 8 weeks) compared with traditional treatment.</p>\n<h3>Predictable</h3>\n<p>The 3D interface of Vincismilesoftware can display the whole treatment process and final results, making it easier for the doctors to communicate with the patients.</p>\n<h3>Removable</h3>\n<p>Vincismile Invisible Orthodontic Appliance is removable, so you can take it out before clean your teeth, which can apparently decrease the incidence of demineralization and caries.</p>\n<h3>Read more information about Vincismile aligners you can here:&nbsp; <a href=\"http://uk.vincismile.com/index/index\" target=\"_self\">http://uk.vincismile.com/index/index</a>&nbsp;or simply send us an Email: marketing@hugedental.com</h3>\n<p style=\"text-align: center;\"><img title=\"VINCISMILE INVISIBLE ALIGNERS\" src=\"https://0.rc.xiniu.com/g2/M00/15/4A/CgAGe1xvodeAa-FJAAOKVGGGeE4734.png\" alt=\"VINCISMILE INVISIBLE ALIGNERS\" /></p>', 'pt', 1, 1, 1, '2020-04-27 11:18:30');
INSERT INTO `product` VALUES (173, 'FILLING Ⅰ', 'FILLING Ⅰ', 69, '20200430110536_87272.jpg,20200430110539_55887.jpg,20200430110542_90824.jpg', '<h2>FillingⅠ</h2>\n<h3>Indication:</h3>\n<p>①Restoration of primary teeth,ClassⅢ,Ⅴand limited ClassⅠcavities.</p>\n<p>②Base linings under composite and amalgam.</p>\n<p>③Core build-up</p>\n<p>④Atraumatic restorative treatment(ART).</p>\n<h3>Specification:</h3>\n<p>15g powder+12g liquid+1 powder measure+1 mixing pad</p>', '<h3 style=\"text-align: center;\">DETAILS</h3>\n<p style=\"text-align: center;\"><img title=\"FILLING Ⅰ\" src=\"https://0.rc.xiniu.com/g3/M00/03/8C/CgAH5122Y1mARY-SAAO2aY57P08130.jpg\" alt=\"FILLING Ⅰ\" /></p>\n<p style=\"text-align: center;\"><img title=\"FILLING Ⅰ\" src=\"https://0.rc.xiniu.com/g1/M00/4F/FF/CgAGTFe9t-SASt5gAADxtY4GpX4017.png\" alt=\"FILLING Ⅰ\" /></p>\n<p style=\"text-align: center;\"><img title=\"FILLING Ⅰ\" src=\"https://0.rc.xiniu.com/g1/M00/4F/FF/CgAGTFe9t-WAKeJgAABmbo0ihAE147.png\" alt=\"FILLING Ⅰ\" /></p>\n<p style=\"text-align: center;\"><img title=\"FILLING Ⅰ\" src=\"https://0.rc.xiniu.com/g1/M00/4F/FF/CgAGTFe9t-WAN0SvAACn0Sh1PdU089.png\" alt=\"FILLING Ⅰ\" /></p>\n<p style=\"text-align: center;\"><img title=\"FILLING Ⅰ\" src=\"https://0.rc.xiniu.com/g1/M00/4F/FF/CgAGTFe9t-WASVnGAAQ3tubnd6w149.png\" alt=\"FILLING Ⅰ\" /></p>\n<p style=\"text-align: center;\"><img title=\"FILLING Ⅰ\" src=\"https://0.rc.xiniu.com/g1/M00/4F/FF/CgAGTFe9t-WAZHdDAAFDustEcKA899.png\" alt=\"FILLING Ⅰ\" /></p>\n<p style=\"text-align: center;\"><img title=\"FILLING Ⅰ\" src=\"https://0.rc.xiniu.com/g1/M00/4F/FF/CgAGTFe9t-aAG-fQAAK-1D5vphU440.png\" alt=\"FILLING Ⅰ\" /></p>\n<p style=\"text-align: center;\"><img title=\"FILLING Ⅰ\" src=\"https://0.rc.xiniu.com/g1/M00/4F/FF/CgAGTFe9t-aAeAsFAAKAVMBsh2o576.jpg\" alt=\"FILLING Ⅰ\" /></p>\n<p style=\"text-align: center;\"><img title=\"FILLING Ⅰ\" src=\"https://0.rc.xiniu.com/g1/M00/4F/FF/CgAGTFe9t-aAGduCAAFFLSp94vc766.png\" alt=\"FILLING Ⅰ\" /></p>\n<h3 style=\"text-align: center;\">Factory</h3>\n<p>With over 30 years in dental materials manufacturing and supplying,Shandong Huge Dental Material Corporation（HUGE for short)is dedicated to be the globally reputable brand in dental industry.</p>\n<p>HUGE implements the most stringent quality standards in the industry.The company has passed the ISO9001,ISO13485,JIS(Japan),CE(EU)and MDL(Canada)and so on.</p>\n<p>&nbsp;</p>\n<p>Owning highest level of R&amp;D department and world-class technology,HUGE can meet different needs and has achieved mass production for synthetic polymer teeth,high quality silicone impression materials,glass ionomer cement,zirconia blocks,PMMA blocks,wax blocks and denture polymers,etc.in China.HUGE has won recognition,trust and high approval from the clients.The dental CAD/CAM PMMA disc/blocks including high quality Zirking zirconia blocks,multi-layers PMMA Blocks and Wax Blocks,are the driving force for the development of digital dental technology.</p>\n<p>Yet,HUGE had never stopped for pursuing going forward,the company has been always fighting for creating and providing more premium and safe products to the world.</p>\n<p>Sincerely we hope that,with joined hands,HUGE can grow up together with all you distinguished global customers and friends.</p>\n<p style=\"text-align: center;\"><img title=\"图片11.png\" src=\"https://0.rc.xiniu.com/g1/M00/4F/07/CgAGS1e9TGiAX0FaAAUL8IYk2TM785.png\" alt=\"图片11.png\" /></p>\n<h3 style=\"text-align: center;\">REGULATION&amp;CERTIFICATE</h3>\n<p style=\"text-align: center;\"><img title=\"图片10.png\" src=\"https://0.rc.xiniu.com/g1/M00/4F/FF/CgAGTFe9uDyAEFECAAN4LhZzkqs230.png\" alt=\"图片10.png\" /></p>', 'pt', 1, 1, 1, '2020-04-30 11:06:11');
INSERT INTO `product` VALUES (174, 'Universal Composite Restorative', 'Universal Composite Restorative', 70, '20200430110845_34221.jpg,20200430110850_37739.jpg', '<h3>Recommend applications:&nbsp;</h3>\n<p>-Direct anterior and posterior restorations,including occlusal and facial surfaces</p>\n<p>-Splinting</p>\n<p>-Indirect restorations such as inlays,onlays,and veneers</p>\n<p>-Core Build-ups</p>\n<h3>Shades available:&nbsp;</h3>\n<div>A1,&nbsp;A2,&nbsp;A3,&nbsp;A3.5,A4</div>\n<div>B1,&nbsp;B2,&nbsp;B3,&nbsp;B4</div>\n<div>C1,&nbsp;C2,&nbsp;C3,&nbsp;C4</div>\n<div>D2,&nbsp;D3,&nbsp;D4</div>\n<div>EA1,&nbsp;EA2,&nbsp;EA3,&nbsp;EB1,&nbsp;EB2,&nbsp;EC1,&nbsp;EC2&nbsp;ET</div>\n<p>OA1,&nbsp;OA2,&nbsp;OA3,&nbsp;OW</p>\n<div>&nbsp;</div>', '<p style=\"text-align: center;\"><img title=\"\" src=\"https://0.rc.xiniu.com/g2/M00/32/25/CgAGe1zFYMGAMAu7AALvHRTIBHo912.png\" alt=\"\" /></p>\n<p style=\"text-align: center;\"><img title=\"\" src=\"https://0.rc.xiniu.com/g2/M00/32/26/CgAGe1zFYQWAW3ZWAASvnKelbVs407.png\" alt=\"\" /></p>', 'pt', 1, 1, 1, '2020-04-30 11:08:53');
INSERT INTO `product` VALUES (175, 'Flowable Composite Restorative', 'Flowable Composite Restorative', 70, '20200430110950_27108.jpg,20200430110957_24432.jpg,20200430111003_38247.jpg', '<h3>New Flowable Composite Restorative&nbsp;Coming Soon</h3>\n<h3>Recommend applications</h3>\n<div>-For all six classes of cavity restorations&nbsp;</div>\n<p>-Fit and fissure sealant&nbsp;</p>\n<p>-Indirect composite or porcelain restoration reparing&nbsp;</p>\n<h3>Shades available</h3>\n<div>Body:16 classical A-D shades</div>\n<div>Enamel:EA1,EB1,EC1,ET(Translucent)</div>\n<div>Dentin:OA2(Opaque,A2)&nbsp;</div>\n<div>&nbsp;</div>', '<h3 style=\"text-align: center;\">Product features&nbsp;</h3>\n<p style=\"text-align: center;\">Low shrinkage</p>\n<p style=\"text-align: center;\">Non-dripping</p>\n<p style=\"text-align: center;\">Increased radiopacity</p>\n<p style=\"text-align: center;\">Outstanding flexural strength</p>\n<p style=\"text-align: center;\">Good thixotropic character&nbsp;</p>\n<p style=\"text-align: center;\">Excellent rheological property</p>\n<p style=\"text-align: center;\">Self leveling for a smooth surface every time</p>\n<p style=\"text-align: center;\">Nanostructured filler loading for a perfect polishability,long lasting shine appearance</p>\n<p style=\"text-align: center;\">&nbsp;</p>\n<p style=\"text-align: center;\"><img title=\"Flowable Composite Restorative coming soon\" src=\"https://0.rc.xiniu.com/g2/M00/25/D3/CgAGfFyHepeACQOjAAD0umCN93s656.png\" alt=\"Flowable Composite Restorative coming soon\" /></p>\n<h3 style=\"text-align: center;\">&nbsp;</h3>\n<h3 style=\"text-align: center;\">Delivery form</h3>\n<p style=\"text-align: center;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Simple:1.5g/piece,with 10pcs of dispensing tips(20g black needle)</p>\n<p style=\"text-align: center;\">Box:1.5g/piece,6 pieses/box,with 50 pcs of dispensing tips(20g black needle)&nbsp;</p>\n<p style=\"text-align: center;\">&nbsp;</p>\n<h3 style=\"text-align: center;\">Shades available</h3>\n<div style=\"text-align: center;\">Body:16 classical A-D shades</div>\n<div style=\"text-align: center;\">Enamel:EA1,EB1,EC1,ET(Translucent)</div>\n<div style=\"text-align: center;\">Dentin:OA2(Opaque,A2)&nbsp;</div>\n<h3 style=\"text-align: center;\">&nbsp;</h3>\n<h3 style=\"text-align: center;\">Contact us</h3>\n<p style=\"text-align: center;\">marketing@hugedental.com</p>', 'pt', 1, 1, 1, '2020-04-30 11:10:27');
INSERT INTO `product` VALUES (176, 'Universal Composite Restorative', 'Universal Composite Restorative', 70, '20200430111104_61135.jpg,20200430111107_89251.jpg', '<h3>Recommend applications</h3>\n<p>-Direct anterior and posterior restorations,including occlusal and facial surfaces</p>\n<p>-Core buold-ups</p>\n<p>-Splinting<br />-Indirect restorations including inlays,onlays and veneers</p>\n<p>&nbsp;</p>\n<h3>Shades available</h3>\n<p>Body:16 classical A-D shades<br />Enamel:EA1,EB1,EC1,ET(Translucent)<br />Dentin:OA2(Opaque,A2)&nbsp;</p>', '<h3 style=\"text-align: center;\">Product features&nbsp;</h3>\n<p style=\"text-align: center;\">Increased radiopacity</p>\n<div style=\"text-align: center;\">Excellent polishability</div>\n<div style=\"text-align: center;\">Exeptional flexural strengths</div>\n<p style=\"text-align: center;\">Long lasting shine appearance</p>\n<p style=\"text-align: center;\">21 colors with extraordinary esthetic for better matching with natural teeth</p>\n<p style=\"text-align: center;\">Low water absorption and solubility guarantee good durability</p>\n<p style=\"text-align: center;\">Mimicking natural teeth with hydroxylapitite filler contained</p>\n<p style=\"text-align: center;\">Unique formula that makes the composite soft and highly moldable without stickness</p>\n<p style=\"text-align: center;\">Easier sculpting to reduce the time needed for trimming and finishing&nbsp;</p>\n<p style=\"text-align: center;\">&nbsp;</p>\n<p style=\"text-align: center;\"><img title=\"\" src=\"https://0.rc.xiniu.com/g2/M00/25/CF/CgAGfFyHdZ-AU76vAAKXxQYFv8Y842.png\" alt=\"\" /></p>\n<p style=\"text-align: center;\">&nbsp;</p>\n<h3 style=\"text-align: center;\">Delivery form</h3>\n<div style=\"text-align: center;\">4g/pc</div>\n<p style=\"text-align: center;\">&nbsp;</p>\n<h3 style=\"text-align: center;\">Shades available</h3>\n<div style=\"text-align: center;\">Body:16 classical A-D shades</div>\n<div style=\"text-align: center;\">Enamel:EA1,EB1,EC1,ET(Translucent)</div>\n<div style=\"text-align: center;\">Dentin:OA2(Opaque,A2)&nbsp;</div>\n<h3 style=\"text-align: center;\">&nbsp;</h3>\n<h3 style=\"text-align: center;\">Contact us&nbsp;</h3>\n<div style=\"text-align: center;\">marketing@hugedental.com</div>', 'pt', 1, 1, 1, '2020-04-30 11:11:33');
INSERT INTO `product` VALUES (177, 'Dual Cure Resin Cement', 'Dual Cure Resin Cement', 70, '20200430111212_75568.jpg,20200430111221_41241.jpg,20200430111227_97859.jpg', '<p>Recommend applications</p>\n<p>-A universal product for permanent cementation of all sorts of dental restorations</p>\n<p>-Two ways of cementation fixing:with Light Cure and without</p>', '<h3 style=\"text-align: center;\">Details</h3>\n<p>&nbsp;</p>\n<h3 style=\"text-align: center;\">Product features</h3>\n<p style=\"text-align: center;\">An all purposes cement material designed for every luting applications&nbsp;</p>\n<p style=\"text-align: center;\">Packaged in the auto-mixed double-barrel cartridge for easy application or in separate syringes for hand-mixing and minimizing waste of product</p>\n<p style=\"text-align: center;\">Low film thickness to ensure restorations perfect fit each time</p>\n<p style=\"text-align: center;\">Excellent physical and mechanical properties ensure luting stability</p>\n<p style=\"text-align: center;\">Seven shades are available in 3 opacity levels for best esthetic results&nbsp;</p>\n<p style=\"text-align: center;\">&nbsp;</p>\n<p style=\"text-align: center;\"><img title=\"\" src=\"https://0.rc.xiniu.com/g2/M00/19/C8/CgAGe1x_iDOAGOX-AAGNhkdBUIE132.png\" alt=\"\" /></p>\n<p style=\"text-align: center;\">&nbsp;</p>\n<h3 style=\"text-align: center;\">Shades available</h3>\n<p style=\"text-align: center;\"><img title=\"\" src=\"https://0.rc.xiniu.com/g2/M00/19/C8/CgAGe1x_h36ASZk2AAD04W_4Drw814.png\" alt=\"\" /></p>\n<p style=\"text-align: center;\">&nbsp;</p>\n<h3 style=\"text-align: center;\">Delivery form</h3>\n<p style=\"text-align: center;\">7g/piece,10 mixing tips</p>\n<p style=\"text-align: center;\">&nbsp;</p>\n<h3 style=\"text-align: center;\">Contact us</h3>\n<p style=\"text-align: center;\">marketing@hugedental.com</p>\n<p style=\"text-align: center;\">&nbsp;</p>\n<p>&nbsp;</p>', 'pt', 1, 1, 1, '2020-04-30 11:12:48');
INSERT INTO `product` VALUES (178, 'Hydrofluoric Acid Etching Gel', 'Hydrofluoric Acid Etching Gel', 70, '20200430111331_99152.jpg,20200430111337_89852.jpg,20200430111343_56392.jpg', '<h3>New Hydrofluoric Acid Etching Gel&nbsp;Coming Soon</h3>\n<h3>Recommend applications</h3>\n<p>Etching pretreatment of porcelain,ceramic surface</p>', '<h3 style=\"text-align: center;\">Details</h3>\n<p style=\"text-align: center;\">&nbsp;</p>\n<h3 style=\"text-align: center;\">Product features</h3>\n<p style=\"text-align: center;\">Suitable for porcelain,ceramic surface&nbsp;</p>\n<p style=\"text-align: center;\">Good rheology character</p>\n<p style=\"text-align: center;\">Easy to rinse out and wash</p>\n<p style=\"text-align: center;\">Designed in red color for an easy appearance on restoration&nbsp;</p>\n<p style=\"text-align: center;\"><img title=\"Hydrofluoric Acid Etching Gel\" src=\"https://0.rc.xiniu.com/g2/M00/19/C1/CgAGe1x_gfiAc0O0AAGfxkH8xIA212.png\" alt=\"Hydrofluoric Acid Etching Gel\" /></p>\n<h3 style=\"text-align: center;\">Delivery form</h3>\n<p style=\"text-align: center;\">1.2ml/pc,10 piess of dispensing tips(25g needle)</p>\n<p style=\"text-align: center;\">&nbsp;</p>\n<h3 style=\"text-align: center;\">Contact us</h3>\n<p style=\"text-align: center;\">marketing@hugedental.com</p>', 'pt', 1, 1, 1, '2020-04-30 11:14:03');
INSERT INTO `product` VALUES (179, 'Phosphoric Acid Etching Gel', 'Phosphoric Acid Etching Gel', 70, '20200430111509_72447.jpg,20200430111514_50623.jpg,20200430111518_10673.jpg', '<h3>New&nbsp;Phosphoric Acid Etching Gel Coming Soon</h3>\n<h3>Recommend applications</h3>\n<p>-Total etch and enamel etch before adhesion or restoration&nbsp;<br />-Clean surface of restoration</p>', '<p style=\"text-align: center;\"><strong><span style=\"font-size: 18px;\">Details</span></strong></p>\n<p>&nbsp;</p>\n<p style=\"text-align: center;\"><strong>Product features</strong></p>\n<p style=\"text-align: center;\">Due to good solubility rinses out easily</p>\n<p style=\"text-align: center;\">Easy to recognize gel on the surface because of a unique blue color</p>\n<p style=\"text-align: center;\">Easy dispensing through the 25g bent tips and apply intra-orally</p>\n<p style=\"text-align: center;\">Good rheology character</p>\n<p style=\"text-align: center;\"><img title=\"Phosphoric Acid Etching Gel\" src=\"https://0.rc.xiniu.com/g2/M00/19/BC/CgAGe1x_fT-AR6TjAAFw_Kle4jc052.png\" alt=\"Phosphoric Acid Etching Gel\" /></p>\n<p>&nbsp;</p>\n<p style=\"text-align: center;\"><strong>Delivery form</strong></p>\n<p style=\"text-align: center;\">3 ml/pc,with 10 pieces of dispensing tips(25g needle)&nbsp;</p>\n<p>&nbsp;</p>\n<p style=\"text-align: center;\"><strong>Contact us</strong></p>\n<p style=\"text-align: center;\">marketing@hugedental.com</p>', 'pt', 1, 1, 1, '2020-04-30 11:15:27');
INSERT INTO `product` VALUES (180, 'Ceramic Coupling Agent', 'Ceramic Coupling Agent', 70, '20200430111626_48602.jpg,20200430111632_33377.jpg,20200430111641_56500.jpg', '<h3>New Ceramic Coupling Agent Coming Soon</h3>\n<h3>Recommend applications</h3>\n<p>-Pre-treat porcelain,ceramic,metal,pre-cured composite surfaces</p>\n<p>-Ceramic coupling Agent was designed&nbsp;to make better bonding strength between adhesion/cement and restorative mentioned above</p>', '<p style=\"text-align: center;\"><span style=\"font-size: 18px;\"><strong>Details</strong></span></p>\n<p>&nbsp;</p>\n<p style=\"text-align: center;\"><strong>Product features</strong></p>\n<p style=\"text-align: center;\">Quick solvent evaporation to minimize the waiting time</p>\n<p style=\"text-align: center;\">A multi-purposes coupling agent for porcelain as well as ceramic restoration&nbsp;</p>\n<p style=\"text-align: center;\"><img title=\"Ceramic Coupling Agent\" src=\"https://0.rc.xiniu.com/g2/M00/19/B4/CgAGe1x_ePCAFdwYAALoqD7f6s8987.png\" alt=\"Ceramic Coupling Agent\" /></p>\n<p>&nbsp;</p>\n<p style=\"text-align: center;\"><strong>Contact us</strong></p>\n<p style=\"text-align: center;\">marketing@hugedental.com</p>\n<p>&nbsp;</p>', 'pt', 1, 1, 1, '2020-04-30 11:17:28');
INSERT INTO `product` VALUES (181, 'Impress Me', 'Impress Me', 71, '20200430111841_79512.jpg,20200430111848_87427.jpg,20200430111853_91413.jpg', '<p>ImpressMe is the brand name of the Impression Kit that includes 4 or several sets of silicon impression material, impression trays and other tools needed for impression purpose that required by order. It is designed specifically for turn long visits to the dentist to an amazing journey.</p>', '<h3 style=\"text-align: center;\">For whom is Impress Me?</h3>\n<p style=\"text-align: center;\">&nbsp;</p>\n<h3 style=\"text-align: center;\">For patients</h3>\n<p style=\"text-align: center;\">Patients can&nbsp;take impression&nbsp;easily&nbsp;at home&nbsp;with ImpressMe,by following the&nbsp;informative manual with each step.</p>\n<p style=\"text-align: center;\">ImpressMe allows patients to save time and do their daily things instead of dentist visits.</p>\n<p style=\"text-align: center;\">&nbsp;</p>\n<p style=\"text-align: center;\">&nbsp;</p>\n<h3 style=\"text-align: center;\">For labs</h3>\n<div style=\"text-align: center;\">Laboratories can produce either click veneer,aligners or other products based on the impression&nbsp;taken from ImpressMe,&nbsp;and send them to the doctors or patients.</div>\n<p style=\"text-align: center;\">&nbsp;</p>\n<p style=\"text-align: center;\">&nbsp;</p>\n<h3 style=\"text-align: center;\">For dentists</h3>\n<p style=\"text-align: center;\">Dentists can use it in their office,or provide ImpressMe to patients and ask them to take impression at home by following the instructions.</p>\n<p style=\"text-align: center;\">&nbsp;</p>\n<h3 style=\"text-align: center;\">Some more useful information about ImpressMe</h3>\n<p style=\"text-align: center;\">&nbsp;</p>\n<h3 style=\"text-align: center;\">Can ImpressMe be customize?</h3>\n<p style=\"text-align: center;\">Sure,you can customize ImpressMe as you wish.There are several choices already exist,and if you want to customize the product according your requirements,just ask in email:&nbsp;marketing@hugedental.com with the details of what you want.</p>\n<p style=\"text-align: center;\">&nbsp;</p>\n<h3 style=\"text-align: center;\">ImpressMe has four kind of packaging:</h3>\n<p style=\"text-align: center;\">Impress Me 25g*4 base+25g*4 catalyst+4 trays&nbsp;&nbsp;&nbsp;</p>\n<p style=\"text-align: center;\">Impress Me Clear 25g*4 base+25g*4 catalyst+4 trays&nbsp;&nbsp;&nbsp;</p>\n<p style=\"text-align: center;\">Impress Me Plus 25g*4 base+25g*4 catalyst+4 trays&nbsp;&nbsp;&nbsp;</p>\n<p style=\"text-align: center;\">ImpressMe Mini&nbsp;25g*3 base+25g*3 catalyst+2 trays&nbsp;&nbsp;</p>\n<p style=\"text-align: center;\">&nbsp;</p>\n<p style=\"text-align: center;\">&nbsp;</p>\n<h3 style=\"text-align: center;\">What is the order procedure?</h3>\n<p style=\"text-align: center;\">It is simple,feel free to contact HUGE sales manager in email:marketing@hugedental.com</p>\n<p style=\"text-align: center;\">&nbsp;</p>\n<h3 style=\"text-align: center;\">Impress Me MOQ 1000 pcs.</h3>\n<p style=\"text-align: center;\">&nbsp;</p>\n<h3 style=\"text-align: center;\">Contact us</h3>\n<p style=\"text-align: center;\">marketing@hugedental.com&nbsp;</p>\n<p>&nbsp;</p>\n<p>&nbsp;</p>', 'pt', 1, 1, 1, '2020-04-30 11:19:16');
INSERT INTO `product` VALUES (182, 'A-SILICONE FOR GINGIVAL MASK', 'A-SILICONE FOR GINGIVAL MASK', 71, '20200430112001_85177.jpg,20200430112005_28282.jpg', '<div>\n<h3>A-Silicone for Gingival Mask</h3>\n</div>\n<p>&nbsp;</p>\n<p><strong>Specification:</strong>50ml/bio-cartridge*2</p>\n<p><strong>Material:</strong>&nbsp;Vinyl polysiloxane(VPS)</p>\n<p><strong>Color:</strong>&nbsp;Base(Pink),Catalyst(White)</p>\n<p><strong>Features:</strong></p>\n<p>①Outstanding fluidity</p>\n<p>②High precision</p>\n<p>③High dimensional stability</p>\n<p>&nbsp;</p>', '<h3 style=\"text-align: center;\">&nbsp;</h3>\n<h3 style=\"text-align: center;\"><span style=\"font-size: 18px;\">DETAILS</span></h3>\n<p>&nbsp;</p>\n<p style=\"text-align: center;\"><img title=\"A-Silicone for Gingival Mask\" src=\"https://0.rc.xiniu.com/g1/M00/58/B0/CgAGTFfWRK6ANY6RAAG68ye93rw952.jpg\" alt=\"A-Silicone for Gingival Mask\" /></p>\n<p>&nbsp;</p>', 'pt', 1, 1, 1, '2020-04-30 11:20:35');
